import * as React from "react";
import { useEffect, useState } from "react";
import Header from "../../components/Header/Header";
import Chart from "../../components/analysis-week/Chart";
import { getData } from "../../utils/getData";
import { getLastMondays } from "../../utils/getLastMondays";
import sumHoursMinutes from "../../utils/sumTime";
import { convertMinToTime } from "../../utils/convertMinToTime";
import { getLastSunday } from "../../utils/getLastSunday";
import { getPercentage } from "../../utils/getPercentage";
import { getAverageTime } from "../../utils/getAverageTime";

import { OneDate } from "../../type";
import { Box, Time, Percent, TimeDiff } from "./style";

function AnalysisWeek() {
  const [data, setData] = useState([]);

  // 데이터 불러오기
  useEffect(() => {
    getData(setData);
  }, []);

  // 이번주 월요일 일요일, 저번주 월요일 일요일 구하기
  const standard = new Date(); // 오늘 날짜
  const monday = new Date(getLastMondays(standard, 1)).getTime();
  const sunday = new Date(getLastSunday(monday)).getTime();
  const lastMonday = new Date(getLastMondays(standard, 2)).getTime();
  const lastSunday = new Date(getLastSunday(lastMonday)).getTime();

  // 데이터에서 이번주, 저번주 리스트 뽑아오기
  const weekList = data.filter((item: OneDate) => {
    const date = new Date(item.date).getTime();
    return date >= monday && date <= sunday;
  });
  console.log(weekList);
  const lastWeekList = data.filter((item: OneDate) => {
    const date = new Date(item.date).getTime();
    return date >= lastMonday && date <= lastSunday;
  });

  // 이번주, 저번주 전체 시간 가져오기
  const entireTimes = weekList.map((item: OneDate) => item.entireTime);

  const lastEntireTimes = lastWeekList.map((item: OneDate) => item.entireTime);

  const getSumTime = (entire: any, time: string, detailTime: string = "") => {
    if (detailTime !== "") {
      return entire.reduce((acc: any, obj: any) => {
        return acc + sumHoursMinutes(obj[time][detailTime]);
      }, 0);
    } else {
      return entire.reduce((acc: any, obj: any) => {
        return acc + sumHoursMinutes(obj[time]);
      }, 0);
    }
  };
  // 이번주 각 항목에 대한 시간 합계 구하기
  const sumTimes = {
    sumImprove: getSumTime(entireTimes, "entireImprove"),
    sumPrivate: getSumTime(entireTimes, "entirePrivate"),
    sumWorks: getSumTime(entireTimes, "entireWorks"),
    sumSleep: getSumTime(entireTimes, "entireSleep"),
  };
  // 저번주 각 항목에 대한 시간 합계 구하기
  const lastSumTimes = {
    sumImprove: getSumTime(lastEntireTimes, "entireImprove"),
    sumPrivate: getSumTime(lastEntireTimes, "entirePrivate"),
    sumWorks: getSumTime(lastEntireTimes, "entireWorks"),
    sumSleep: getSumTime(lastEntireTimes, "entireSleep"),
  };

  const sumDetailTimes = {
    sumStudy: getSumTime(weekList, "improve", "study"),
    sumGame: getSumTime(weekList, "private", "game"),
    sumReading: getSumTime(weekList, "improve", "reading"),
    sumWakeUp: getSumTime(weekList, "sleeping", "wake"),
    sumNight: getSumTime(weekList, "sleeping", "sleep"),
  };

  const lastSumDetailTimes = {
    sumStudy: getSumTime(lastWeekList, "improve", "study"),
    sumGame: getSumTime(lastWeekList, "private", "game"),
    sumReading: getSumTime(lastWeekList, "improve", "reading"),
    sumWakeUp: getSumTime(lastWeekList, "sleeping", "wake"),
    sumNight: getSumTime(lastWeekList, "sleeping", "sleep"),
  };

  // 저번주와의 퍼센트 비교값 구하기

  const mainTimesPercent = {
    improvePercent: getPercentage(sumTimes.sumImprove, lastSumTimes.sumImprove),
    privatePercent: getPercentage(sumTimes.sumPrivate, lastSumTimes.sumPrivate),
    worksPercent: getPercentage(sumTimes.sumWorks, lastSumTimes.sumWorks),
    sleepPercent: getPercentage(sumTimes.sumSleep, lastSumTimes.sumSleep),
  };

  const detailTimesPercent = {
    studyPercent: getPercentage(
      sumDetailTimes.sumStudy,
      lastSumDetailTimes.sumStudy
    ),
    gamePercent: getPercentage(
      sumDetailTimes.sumGame,
      lastSumDetailTimes.sumGame
    ),
    readingPercent: getPercentage(
      sumDetailTimes.sumReading,
      lastSumDetailTimes.sumReading
    ),
    wakePercent: getPercentage(
      sumDetailTimes.sumWakeUp,
      lastSumDetailTimes.sumWakeUp
    ),
    nightPercent: getPercentage(
      sumDetailTimes.sumNight,
      lastSumDetailTimes.sumNight
    ),
  };
  const sleepingAverage = {
    thisWakeUp: getAverageTime(sumDetailTimes.sumWakeUp),
    lastWakeUp: getAverageTime(lastSumDetailTimes.sumWakeUp),
    thisNight: getAverageTime(sumDetailTimes.sumNight),
    lastNight: getAverageTime(lastSumDetailTimes.sumNight),
  };
  // 기타 시간사용 통계 퍼센트

  return (
    <>
      <Header />
      <Box>
        <h2>이번주 시간사용 통계</h2>
        <h3>
          자기계발: <Time>{convertMinToTime(sumTimes.sumImprove)}</Time>
          <Percent data={mainTimesPercent.improvePercent}>
            {mainTimesPercent.improvePercent}%
          </Percent>
        </h3>
        <h3>
          개인시간: <Time>{convertMinToTime(sumTimes.sumPrivate)}</Time>
          <Percent data={mainTimesPercent.privatePercent}>
            {mainTimesPercent.privatePercent}%
          </Percent>
        </h3>

        <h3>
          일: <Time>{convertMinToTime(sumTimes.sumWorks)}</Time>
          <Percent data={mainTimesPercent.worksPercent}>
            {mainTimesPercent.worksPercent}%
          </Percent>
        </h3>

        <h3>
          취침: <Time>{convertMinToTime(sumTimes.sumSleep)}</Time>
          <Percent data={mainTimesPercent.sleepPercent}>
            {mainTimesPercent.sleepPercent}%
          </Percent>
        </h3>
      </Box>
      <Box>
        <h2>기타 시간사용 통계</h2>
        <h3>
          공부시간: <Time>{convertMinToTime(sumDetailTimes.sumStudy)}</Time>
          <Percent data={studyPercent}>{studyPercent}%</Percent>
        </h3>
        <h3>
          게임시간: <Time>{convertMinToTime(sumDetailTimes.sumGame)}</Time>
          <Percent data={gamePercent}>{gamePercent}%</Percent>
        </h3>
        <h3>
          독서 및 신문:{" "}
          <Time>{convertMinToTime(sumDetailTimes.sumReading)}</Time>
          <Percent data={readingPercent}>{readingPercent}%</Percent>
        </h3>
        <h3>
          평균취침: <Time>{thisWakeUp}</Time>
          <TimeDiff data={wakePercent}>{wakePercent}%</TimeDiff>
        </h3>
        <h3>
          평균기상: <Time>{thisNight}</Time>
          <TimeDiff data={nightPercent}>{nightPercent}%</TimeDiff>
        </h3>
      </Box>
    </>
  );
}

export default AnalysisWeek;
