import React, { useEffect, useState, useInterval } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import getDay from "../utils/getDay";
import msToTime from "../utils/msToTime";

function TimeInput() {
  const today = new Date().toLocaleDateString();
  const day = getDay();

  const tomorrow = new Date(today).getTime() + 86400000;

  const propsTime = tomorrow - Date.now();

  const [remainTime, setRemainTime] = useState(null);

  setInterval(() => {
    setRemainTime(msToTime(tomorrow - Date.now()));
  }, 1000);

  return (
    <StyledInput time={propsTime}>
      <h2>오늘 하루 잔여 시간</h2>

      <span className="progress-bar">
        <span className="remaining-time">{remainTime}</span>
        <span className="gauge"></span>
        <i className="fas fa-running"></i>
      </span>

      <div className="time-input">
        <h3>{today + day}</h3>
        <div className="record-box">
          기록 <span className="record">{433}</span>일 째
        </div>

        <Link to="/input">
          <button>시간기록하기</button>
        </Link>
      </div>
    </StyledInput>
  );
}

const StyledInput = styled.div`
  $buttonColor: #1671cc;

  margin: 1rem;
  margin-top: 2rem;

  h2 {
    font-size: 0.8rem;
    margin-bottom: 0.5rem;
  }
  .progress-bar {
    display: inline-block;
    width: 23rem;
    height: 2rem;
    background-color: #1671cc;
    margin-bottom: 1rem;
    box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px,
      rgba(60, 64, 67, 0.15) 0px 2px 6px 2px;
  }

  .gauge {
    display: inline-block;
    width: ${(props) => {
      return 100 - ((props.time / 86400000) * 100).toFixed(3);
    }}%;
    transition-property: width;
    transition-duration: 1s;
    transition-timing-function: linear;
    height: 2rem;

    background-color: #d6d3d3;
  }

  .fa-running {
    display: inline-block;
    position: absolute;
    font-size: 2rem;
    z-index: 1;
  }

  .remaining-time {
    position: absolute;
    left: 30px;
    color: #ffffffeb;
    z-index: 2;
    font-weight: bolder;
    font-size: 1.3rem;
  }

  .time-input {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    padding: 1rem;
    background-color: rgba(255, 255, 255, 0.096);
    width: 23rem;
    height: 11rem;
    box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px,
      rgba(60, 64, 67, 0.15) 0px 2px 6px 2px;

    h3 {
      font-size: 1rem;
    }

    .record-box {
      font-weight: bold;
    }

    .record {
      font-size: 2rem;
      color: rgb(59, 63, 171);
      font-weight: bold;
      margin-right: 5px;
      margin-left: 5px;
    }

    button {
      width: 14rem;
      background-color: #1671cc;
      color: white;
      border: 0;
      padding: 0.5rem;
      border-radius: 5px;
      font-size: 1rem;
      cursor: pointer;
      box-shadow: rgba(0, 0, 0, 0.15) 1.95px 1.95px 2.6px;
    }
  }
`;

export default TimeInput;
