// 현재 날짜와 week(1,2,3...)주를 파라미터로 받는다.
export function getLastMondays(date: Date, week: number): number {
  const ONE_DAY_MS = 86400000;
  const msDate = date.getTime();

  const result =
    date.getDay() !== 0
      ? new Date(year, month, day - 7 * ONE_DAY_MS * week - (date.getDay() - 1))
      : new Date(year, month, day - 7 * week - 6);

  return result;
}
