import styled from "styled-components";

export const Container = styled.div`
  margin-top: 5rem;
  h3 {
    font-size: 1rem;
    margin-bottom: 0.8rem;
  }

  .month {
    display: flex;
    justify-content: flex-start;
    width: 100%;
  }

  li {
    font-size: 0.5rem;
    margin-right: 1rem;
  }
`;

export const ContributionBox = styled.ul`
  display: flex;
  width: 100%;
  height: 8rem;
  flex-direction: column;
  flex-wrap: wrap;
  /* border: 0.3px gray solid; */

  align-items: center;

  li {
    display: inline-block;
    width: 0.6rem;
    height: 0.6rem;
    margin: 2px;
    box-shadow: rgba(0, 0, 0, 0.18) 0px 2px 4px;
    background-color: rgba(84, 172, 237, 1);
  }
`;
