import styled from "styled-components";

export const MotivationBox = styled.div`
  display: flex;
  flex-direction: column;
  width: 40rem;
  h3 {
    font-size: 1rem;
    margin-bottom: 0.8rem;
  }
  .content-box {
    background-color: #8080803b;
    padding: 3rem;
  }

  .content {
    font-weight: bold;
  }

  .author {
    margin-top: 1rem;
    float: right;
  }
`;
