import * as React from "react";
import { useState, useEffect } from "react";
import Header from "../../components/Header/Header";
import InputForm from "../../components/InputForm/InputForm";
import styled from "styled-components";
import sumHoursMinutes from "../../utils/sumTime";
import Calender from "../../components/Calendar/Calendar";
import { getData } from "../../utils/getData";
import { minutesToHours } from "../../utils/minutesToHours";
import { OneDate } from "../../type";

function InputPage() {
  const [data, setData] = useState(null);
  const [calender, setCalender] = useState("2022.1");
  const [selectedDate, setSelectedDate] = useState(new Date().getDate());
  const [finishedDay, setFinishedDays] = useState([]);

  const [times, setTimes] = useState({
    improveTime: {
      study: "",
      workout: "",
      reading: "",
      rest: "",
    },
    privateTime: {
      privates: "",
      game: "",
    },
    sleepTime: {
      night: "",
      nap: "",
      wake: "",
      sleep: "",
    },
    worksTime: {
      works: "",
      houseWork: "",
    },
  });

  // const [improveTime, setImproveTime] = useState({
  //   study: "",
  //   workout: "",
  //   reading: "",
  //   rest: "",
  // });
  // const [privateTime, setPrivateTime] = useState({
  //   privates: "",
  //   game: "",
  // });

  // const [sleepTime, setSleepTime] = useState({
  //   night: "",
  //   nap: "",
  //   wake: "",
  //   sleep: "",
  // });
  // const [worksTime, setWorksTime] = useState({
  //   works: "",
  //   houseWork: "",
  // });

  useEffect(() => {
    getData(setData);
  }, []);

  // 입력 항목 추출
  const { study, workout, reading, rest } = improveTime;
  const { privates, game } = privateTime;
  const { night, nap, wake, sleep } = sleepTime;
  const { works, houseWork } = worksTime;

  const entireTime = {
    entireImprove: minutesToHours(
      Object.values(improveTime).reduce(
        (acc, val) => acc + sumHoursMinutes(val),
        0
      )
    ),
    entirePrivate: minutesToHours(
      Object.values(privateTime).reduce(
        (acc, val) => acc + sumHoursMinutes(val),
        0
      )
    ),
    entireWorks: minutesToHours(
      Object.values(worksTime).reduce(
        (acc, val) => acc + sumHoursMinutes(val),
        0
      )
    ),

    entireSleep: minutesToHours(
      sumHoursMinutes(sleepTime.night) + sumHoursMinutes(sleepTime.nap)
    ),
  };

  // Input 핸들러 컬렉션
  const inputHandlers = {
    worksHandler(e: React.ChangeEvent<HTMLInputElement>) {
      const { value, name } = e.target;
      setWorksTime({
        ...worksTime,
        [name]: value,
      });
    },
    sleepHandler(e: React.ChangeEvent<HTMLInputElement>) {
      const { value, name } = e.target;
      setSleepTime({
        ...sleepTime,
        [name]: value,
      });
    },
    privateHandler(e: React.ChangeEvent<HTMLInputElement>) {
      const { value, name } = e.target;
      setPrivateTime({
        ...privateTime,
        [name]: value,
      });
    },
    improveHandler(e: React.ChangeEvent<HTMLInputElement>) {
      const { value, name } = e.target;
      setImproveTime({
        ...improveTime,
        [name]: value,
      });
    },
  };
  const getId = (data: any) => {
    if (typeof data !== null) {
      return data
        .map((item: OneDate) => {
          if (item.date === `${calender}.${selectedDate}`) {
            return item.id;
          }
          return "";
        })
        .join("");
    }
  };

  const getFormInfo = async (data: any) => {
    if (typeof data !== null) {
      const id = await getId(data);

      if (id) {
        const response = await fetch(`http://localhost:3000/timedata/${id}`);
        const result = await response.json();
        await setImproveTime(result.improve);
        await setPrivateTime(result.private);
        await setSleepTime(result.sleeping);
        await setWorksTime(result.working);
      } else {
        await setImproveTime({
          study: "",
          workout: "",
          reading: "",
          rest: "",
        });
        await setPrivateTime({
          privates: "",
          game: "",
        });
        await setSleepTime({
          night: "",
          nap: "",
          wake: "",
          sleep: "",
        });
        await setWorksTime({ works: "", houseWork: "" });
      }
    }
  };

  // 폼 제출 핸들러
  const submitHandler = async () => {
    // post할 데이터
    const timeData = {
      date: `${calender}.${selectedDate}`,
      improve: {
        study,
        workout,
        reading,
        rest,
      },
      private: {
        privates,
        game,
      },
      sleeping: {
        night,
        nap,
        wake,
        sleep,
      },
      working: {
        works,
        houseWork,
      },
      entireTime,
    };

    // fetch POST
    await fetch("http://localhost:3000/timedata", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(timeData),
    });

    await getData(setData);
    setSelectedDate(selectedDate + 1);
    console.log(data);
  };

  const updateHandler = async () => {
    // post할 데이터

    const id = await getId(data);

    const timeData = {
      date: `${calender}.${selectedDate}`,
      improve: {
        study,
        workout,
        reading,
        rest,
      },
      private: {
        privates,
        game,
      },
      sleeping: {
        night,
        nap,
        wake,
        sleep,
      },
      working: {
        works,
        houseWork,
      },
      entireTime,
    };

    // fetch POST
    await fetch(`http://localhost:3000/timedata/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(timeData),
    });

    await getData(setData);
  };
  return (
    <>
      <Header />
      <Container>
        <InputForm
          handlers={inputHandlers}
          entireTime={entireTime}
          submitHandler={submitHandler}
          selectedDate={selectedDate}
          calender={calender}
          updateHandler={updateHandler}
          finishedDay={finishedDay}
          improveTime={improveTime}
          privateTime={privateTime}
          sleepTime={sleepTime}
          worksTime={worksTime}
        />
        <Calender
          calender={calender}
          setCalender={setCalender}
          setSelectedDate={setSelectedDate}
          selectedDate={selectedDate}
          data={data}
          finishedDay={finishedDay}
          setFinishedDays={setFinishedDays}
          getFormInfo={getFormInfo}
        />
      </Container>
    </>
  );
}

const Container = styled.div`
  display: flex;
`;

export default InputPage;
