export const getAverageTime = (wakeupTime: any, isThisWeek: boolean) => {
  const today = new Date().getDay();

  const day = today === 0 || isThisWeek ? 7 : today;

  return `${Math.floor(wakeupTime / day / 60)}시 ${wakeupTime % 60}분`;
};
