            </h3>
          </Box>
        </BoxContainer>
        <ChartBox>
          <Chart thisWeek={weekList} lastWeek={lastWeekList}></Chart>
        </ChartBox>
      </Container>
    </>
  );
}

export default AnalysisWeek;
