import * as React from "react";
import { useEffect, useState } from "react";
import Header from "../../components/Header/Header";
import Chart from "../../components/analysis-week/Chart";
import { getData } from "../../utils/getData";
import { getLastMondays } from "../../utils/getLastMondays";
import sumHoursMinutes from "../../utils/sumTime";
import { convertMinToTime } from "../../utils/convertMinToTime";
import { getLastSunday } from "../../utils/getLastSunday";
import { getPercentage } from "../../utils/getPercentage";
import { getAverageTime } from "../../utils/getAverageTime";

import { OneDate } from "../../type";
import { Box, Time, Percent, TimeDiff } from "./style";

function AnalysisWeek() {
  const [data, setData] = useState([]);

  // 데이터 불러오기
  useEffect(() => {
    getData(setData);
  }, []);

  // 이번주 월요일 일요일, 저번주 월요일 일요일 구하기
  const standard = new Date(); // 오늘 날짜
  const monday = new Date(getLastMondays(standard, 1)).getTime();
  const sunday = new Date(getLastSunday(monday)).getTime();
  const lastMonday = new Date(getLastMondays(standard, 2)).getTime();
  const lastSunday = new Date(getLastSunday(lastMonday)).getTime();

  // 데이터에서 이번주, 저번주 리스트 뽑아오기
  const weekList = data.filter((item: OneDate) => {
    const date = new Date(item.date).getTime();
    return date >= monday && date <= sunday;
  });
  console.log(weekList);
  const lastWeekList = data.filter((item: OneDate) => {
    const date = new Date(item.date).getTime();
    return date >= lastMonday && date <= lastSunday;
  });

  // 이번주, 저번주 전체 시간 가져오기
  const entireTimes = weekList.map((item: OneDate) => item.entireTime);

  const lastEntireTimes = lastWeekList.map((item: OneDate) => item.entireTime);

  const getSumTime = (time: string) => {
    entireTimes.reduce((acc, obj) => {
      return acc + sumHoursMinutes(obj[time]);
    }, 0);
  };
  // 이번주 각 항목에 대한 시간 합계 구하기
  const sumImprove = entireTimes.reduce((acc, obj) => {
    return acc + sumHoursMinutes(obj.entireImprove);
  }, 0);

  const sumPrivate = entireTimes.reduce((acc, obj) => {
    return acc + sumHoursMinutes(obj.entirePrivate);
  }, 0);

  const sumWorks = entireTimes.reduce((acc, obj) => {
    return acc + sumHoursMinutes(obj.entireWorks);
  }, 0);

  const sumSleep = entireTimes.reduce((acc, obj) => {
    return acc + sumHoursMinutes(obj.entireSleep);
  }, 0);

  const sumTimes = {
    sumImprove: getSumTime("entireImprove"),
    sumPrivate: getSumTime("entirePrivate"),
    sumWorks: getSumTime("entireWorks"),
    sumSleep: getSumTime("entireSleep"),
  };

  // 저번주 각 항목에 대한 시간 합계 구하기
  const lastSumImprove = lastEntireTimes.reduce((acc, obj) => {
    return acc + sumHoursMinutes(obj.entireImprove);
  }, 0);

  const lastSumPrivate = lastEntireTimes.reduce((acc, obj) => {
    return acc + sumHoursMinutes(obj.entirePrivate);
  }, 0);

  const lastSumWorks = lastEntireTimes.reduce((acc, obj) => {
    return acc + sumHoursMinutes(obj.entireWorks);
  }, 0);

  const lastSumSleep = lastEntireTimes.reduce((acc, obj) => {
    return acc + sumHoursMinutes(obj.entireSleep);
  }, 0);

  const sumStudy = weekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.improve.study);
  }, 0);

  const sumGame = weekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.private.game);
  }, 0);

  const sumReading = weekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.improve.reading);
  }, 0);

  const sumWakeUp = weekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.sleeping.wake);
  }, 0);

  const sumNight = weekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.sleeping.sleep);
  }, 0);

  const sumLastStudy = lastWeekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.improve.study);
  }, 0);

  const sumLastGame = lastWeekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.private.game);
  }, 0);
  const sumLastReading = lastWeekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.improve.reading);
  }, 0);

  const sumLastWakeUp = lastWeekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.sleeping.wake);
  }, 0);

  const sumLastNight = lastWeekList.reduce((acc, obj: OneDate) => {
    return acc + sumHoursMinutes(obj.sleeping.sleep);
  }, 0);

  // 저번주와의 퍼센트 비교값 구하기
  const improvePercent = getPercentage(sumImprove, lastSumImprove);
  const privatePercent = getPercentage(sumPrivate, lastSumPrivate);
  const worksPercent = getPercentage(sumWorks, lastSumWorks);
  const sleepPercent = getPercentage(sumSleep, lastSumSleep);

  // 기타 시간사용 통계 퍼센트
  const studyPercent = getPercentage(sumStudy, sumLastStudy);
  const gamePercent = getPercentage(sumGame, sumLastGame);
  const readingPercent = getPercentage(sumReading, sumLastReading);

  const thisWakeUp = getAverageTime(sumWakeUp);
  const lastWakeUp = getAverageTime(sumLastWakeUp);
  const thisNight = getAverageTime(sumNight);
  const lastNight = getAverageTime(sumLastNight);

  const wakePercent = getPercentage(sumWakeUp, sumLastWakeUp);
  const nightPercent = getPercentage(sumNight, sumLastNight);

  return (
    <>
      <Header />
      <Box>
        <h2>이번주 시간사용 통계</h2>
        <h3>
          자기계발: <Time>{convertMinToTime(sumImprove)}</Time>
          <Percent data={improvePercent}>{improvePercent}%</Percent>
        </h3>
        <h3>
          개인시간: <Time>{convertMinToTime(sumPrivate)}</Time>
          <Percent data={privatePercent}>{privatePercent}%</Percent>
        </h3>

        <h3>
          일: <Time>{convertMinToTime(sumWorks)}</Time>
          <Percent data={worksPercent}>{worksPercent}%</Percent>
        </h3>

        <h3>
          취침: <Time>{convertMinToTime(sumSleep)}</Time>
          <Percent data={sleepPercent}>{sleepPercent}%</Percent>
        </h3>
      </Box>
      <Box>
        <h2>기타 시간사용 통계</h2>
        <h3>
          공부시간: <Time>{convertMinToTime(sumStudy)}</Time>
          <Percent data={studyPercent}>{studyPercent}%</Percent>
        </h3>
        <h3>
          게임시간: <Time>{convertMinToTime(sumGame)}</Time>
          <Percent data={gamePercent}>{gamePercent}%</Percent>
        </h3>
        <h3>
          독서 및 신문: <Time>{convertMinToTime(sumReading)}</Time>
          <Percent data={readingPercent}>{readingPercent}%</Percent>
        </h3>
        <h3>
          평균취침: <Time>{thisWakeUp}</Time>
          <TimeDiff data={wakePercent}>{wakePercent}%</TimeDiff>
        </h3>
        <h3>
          평균기상: <Time>{thisNight}</Time>
          <TimeDiff data={nightPercent}>{nightPercent}%</TimeDiff>
        </h3>
      </Box>
    </>
  );
}

export default AnalysisWeek;
