// 현재 날짜와 week(1,2,3...)주를 파라미터로 받는다.
export function getLastMondays(date: Date, week: number): number {
  const ONE_DAY_MS = 86400000 * week;
  const msDate = date.getTime();

  const result =
    date.getDay() !== 0
      ? msDate - 7 * ONE_DAY_MS - (date.getDay() - 1)
      : msDate - 7 * ONE_DAY_MS - 6;

  return result;
}
