import { MotivationBox } from "../Motivation/style";
import styled from "styled-components";

export const WeekStatusBox = styled(MotivationBox)`
  width: 80%;
  margin-top: 3rem;

  h2 {
    font-size: 1rem;
    margin-bottom: 1rem;
  }

  .progress-bar {
    display: inline-block;
    position: relative;
    width: 23rem;
    height: 2rem;
    background-color: #1671cc;
    margin-bottom: 1rem;
    box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px,
      rgba(60, 64, 67, 0.15) 0px 2px 6px 2px;
  }

  .gauge {
    display: inline-block;
    /* width: ${(props: { time: number }): number => {
      const operation: string = ((props.time / 86400000) * 100).toFixed(3);
      return 100 - Number(operation);
    }}%; */
    transition-property: width;
    transition-duration: 1s;
    transition-timing-function: linear;
    height: 2rem;

    background-color: #d6d3d3;
  }

  .remaining-time {
    position: absolute;
    right: 10px;
    color: #ffffffeb;
    z-index: 2;
    font-weight: bolder;
    font-size: 1.3rem;
  }
`;
