import * as React from "react";
import { useState } from "react";
import { Bar } from "react-chartjs-2";
import styled from "styled-components";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { getMainSumTimes, getDetailSumTimes } from "../../utils/getSumTimesObj";
import { getEntireTimes } from "../../utils/getEntireTimes";
import { convertMinToTime } from "../../utils/convertMinToTime";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function Chart({
  thisList,
  lastList,
  pageType,
  restList = "undefined",
  selectedDate,
}: any) {
  const year = Number(selectedDate.current.year);
  const month = Number(selectedDate.current.month);

  const { twoMonthAgo, threeMonthAgo, fourMonthAgo, fiveMonthAgo } = restList;

  const monthList = [
    {
      beforeMonth: 5,
      sumTimes: {
        ...getMainSumTimes(getEntireTimes(fiveMonthAgo)),
        ...getDetailSumTimes(fiveMonthAgo),
      },
    },
    {
      beforeMonth: 4,
      sumTimes: {
        ...getMainSumTimes(getEntireTimes(fourMonthAgo)),
        ...getDetailSumTimes(fourMonthAgo),
      },
    },
    {
      beforeMonth: 3,
      sumTimes: {
        ...getMainSumTimes(getEntireTimes(threeMonthAgo)),
        ...getDetailSumTimes(threeMonthAgo),
      },
    },
    {
      beforeMonth: 2,
      sumTimes: {
        ...getMainSumTimes(getEntireTimes(twoMonthAgo)),
        ...getDetailSumTimes(twoMonthAgo),
      },
    },
    {
      beforeMonth: 1,
      sumTimes: {
        ...getMainSumTimes(getEntireTimes(lastList)),
        ...getDetailSumTimes(lastList),
      },
    },
    {
      beforeMonth: 0,
      sumTimes: {
        ...getMainSumTimes(getEntireTimes(thisList)),
        ...getDetailSumTimes(thisList),
      },
    },
  ];

  const [selectedList, setSelectedList] = useState({
    list: "IMPROVE_TIME",
    title: "자기계발",
  });

  const getChartData = (dayData: any) => {
    switch (selectedList.list) {
      case "IMPROVE_TIME":
        return Number(dayData.entireTime.entireImprove.split(":")[0]);
      case "PRIVATE_TIME":
        return Number(dayData.entireTime.entirePrivate.split(":")[0]);
      case "SLEEP_TIME":
        return Number(dayData.entireTime.entireSleep.split(":")[0]);
      case "WORK_TIME":
        return Number(dayData.entireTime.entireWorks.split(":")[0]);
      case "STUDY_TIME":
        return Number(dayData.improve.study.split(":")[0]);
      case "READING_TIME":
        return Number(dayData.improve.reading.split(":")[0]);
    }
  };

  const getMonthChartData = (monthData: any) => {
    switch (selectedList.list) {
      case "IMPROVE_TIME":
        return Number(
          convertMinToTime(monthData.sumTimes.sumImprove).split("시간")[0]
        );
      case "PRIVATE_TIME":
        return Number(
          convertMinToTime(monthData.sumTimes.sumPrivate).split("시간")[0]
        );
      case "SLEEP_TIME":
        return Number(
          convertMinToTime(monthData.sumTimes.sumSleep).split("시간")[0]
        );
      case "WORK_TIME":
        return Number(
          convertMinToTime(monthData.sumTimes.sumWorks).split("시간")[0]
        );
      case "STUDY_TIME":
        return Number(
          convertMinToTime(monthData.sumTimes.sumStudy).split("시간")[0]
        );
      case "READING_TIME":
        return Number(
          convertMinToTime(monthData.sumTimes.sumReading).split("시간")[0]
        );
    }
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top" as const,
      },
      title: {
        display: true,
        text: `${selectedList.title} 시간 통계`,
      },
    },
  };

  const weekDataSets = [
    {
      label: `지난${pageType[1]}`,
      data: lastList.map((day: any) => {
        console.log(day.entireTime.entireImprove);
        return getChartData(day);
      }),
      backgroundColor: "rgba(255, 99, 132, 0.5)",
    },
    {
      label: `이번${pageType[1]}`,
      data: thisList.map((day: any) => {
        console.log(Number(day.entireTime.entireImprove.split(":")[0]));
        return getChartData(day);
      }),
      backgroundColor: "rgba(53, 162, 235, 0.5)",
    },
  ];

  const monthDataSets = [
    {
      label: `${selectedList.title}`,
      data: monthList.map((month) => {
        return getMonthChartData(month);
      }),
      backgroundColor: () => {
        switch (selectedList.list) {
          case "IMPROVE_TIME":
            return "rgba(251, 235, 1, 0.5)";
          case "PRIVATE_TIME":
            return "rgba(241, 66, 43, 0.5)";
          case "SLEEP_TIME":
            return "rgba(107, 29, 195, 0.5)";
          case "WORK_TIME":
            return " rgba(0, 226, 207, 0.5)";
          case "STUDY_TIME":
            return "rgba(228, 130, 2, 0.5)";
          case "READING_TIME":
            return "rgba(49, 37, 2, 0.5)";
        }
      },
    },
  ];

  const monthLabels = [
    `${month - 5}월`,
    `${month - 4}월`,
    `${month - 3}월`,
    `${month - 2}월`,
    `${month - 1}월`,
    `${month}월`,
  ];

  const weekLabels = ["월", "화", "수", "목", "금", "토", "일"];

  const data = {
    labels: monthLabels,
    datasets:
      pageType[2] === "Week"
        ? weekDataSets
        : pageType[2] === "Month"
        ? monthDataSets
        : monthDataSets,
  };

  const listClickHandler = (e: any): void => {
    if (e.target.tagName === "LI") {
      const clickedLabel: string = e.target.dataset.labels;
      setSelectedList({
        ...selectedList,
        list: clickedLabel,
        title: e.target.textContent,
      });
    }
  };

  return (
    <>
      <TopBar onClick={listClickHandler}>
        <li data-labels="IMPROVE_TIME">자기계발</li>
        <li data-labels="PRIVATE_TIME">개인시간</li>
        <li data-labels="WORK_TIME">일</li>
        <li data-labels="SLEEP_TIME">취침</li>
        <li data-labels="STUDY_TIME">공부시간</li>
        <li data-labels="READING_TIME">독서 및 신문</li>
      </TopBar>
      <Bar options={options} data={data} />
    </>
  );
}

const TopBar = styled.ul`
  display: flex;
  position: relative;
  justify-content: space-around;
  margin-bottom: 2rem;
  li {
    width: 5rem;
    color: #36424f;
    text-align: center;
    font-weight: bold;
    font-size: 0.8rem;
    padding-bottom: 0.2rem;
    border-bottom: 2px solid transparent;
    cursor: pointer;
  }

  li:hover {
    border-bottom: 2px solid #1671cc;
    transition-property: border-bottom;
    transition-duration: 1s;
  }
`;

export default Chart;
