import * as React from "react";
import { useState } from "react";
import { Bar } from "react-chartjs-2";
import styled from "styled-components";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: "top" as const,
    },
    title: {
      display: true,
      text: `자기계발 시간 통계`,
    },
  },
};

const labels = ["월", "화", "수", "목", "금", "토", "일"];

function Chart({ thisWeek, lastWeek }: any) {
  const [selectedList, setSelectedList] = useState("IMPROVE_TIME");

  const getChartData = (dayData: any) => {
    switch (selectedList) {
      case "IMPROVE_TIME":
        return Number(dayData.entireTime.entireImprove.split(":")[0]);
      case "PRIVATE_TIME":
        return Number(dayData.entireTime.entirePrivate.split(":")[0]);
      case "SLEEP_TIME":
        return Number(dayData.entireTime.entireSleep.split(":")[0]);
      case "WORK_TIME":
        return Number(dayData.entireTime.entireWorks.split(":")[0]);
      case "STUDY_TIME":
        return Number(dayData.improve.study.split(":")[0]);
      case "READING_TIME":
        return Number(dayData.improve.reading.split(":")[0]);
    }
  };

  const data = {
    labels,
    datasets: [
      {
        label: "지난 주",
        data: lastWeek.map((day: any) => {
          console.log(day.entireTime.entireImprove);
          return getChartData(day);
        }),
        backgroundColor: "rgba(255, 99, 132, 0.5)",
      },
      {
        label: "이번 주",
        data: thisWeek.map((day: any) => {
          console.log(Number(day.entireTime.entireImprove.split(":")[0]));
          return getChartData(day);
        }),
        backgroundColor: "rgba(53, 162, 235, 0.5)",
      },
    ],
  };

  const listClickHandler = (e: any): void => {
    if (e.target.tagName === "LI") {
      const clickedLabel: string = e.target.dataset.labels;
      setSelectedList(clickedLabel);
    }
  };

  return (
    <>
      <TopBar onClick={listClickHandler}>
        <li data-labels="IMPROVE_TIME">자기계발</li>
        <li data-labels="PRIVATE_TIME">개인시간</li>
        <li data-labels="WORK_TIME">일</li>
        <li data-labels="SLEEP_TIME">취침</li>
        <li data-labels="STUDY_TIME">공부시간</li>
        <li data-labels="READING_TIME">독서및신문</li>
      </TopBar>
      <Bar options={options} data={data} />
    </>
  );
}

const TopBar = styled.ul`
  display: flex;
  position: sticky;
  justify-content: space-around;
  margin-bottom: 2rem;
  li {
    font-weight: bold;
    font-size: 0.8rem;
    cursor: pointer;
  }

  li:hover {
    border-bottom: 2px solid blue;
  }
`;

export default Chart;
