import * as React from "react";
import { Bar } from "react-chartjs-2";
import styled from "styled-components";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { isTemplateTail } from "typescript";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: "top" as const,
    },
    title: {
      display: true,
      text: `자기계발 시간 통계`,
    },
  },
};

const labels = ["월", "화", "수", "목", "금", "토", "일"];

function Chart({ thisWeek, lastWeek }: any) {
  const data = {
    labels,
    datasets: [
      {
        label: "지난 주",
        data: lastWeek.map((day: any) => {
          console.log(day.entireTime.entireImprove);
          return day.entireTime.entireImprove;
        }),
        backgroundColor: "rgba(255, 99, 132, 0.5)",
      },
      {
        label: "이번 주",
        data: labels.map(() => Math.random() * 10),
        backgroundColor: "rgba(53, 162, 235, 0.5)",
      },
    ],
  };

  return (
    <>
      <TopBar>
        <li>자기계발</li>
        <li>개인시간</li>
        <li>일</li>
        <li>취침</li>
        <li>공부시간</li>
        <li>독서및신문</li>
      </TopBar>
      <Bar options={options} data={data} />
    </>
  );
}

const TopBar = styled.ul`
  display: flex;

  li {
    margin-left: 1rem;
  }
`;

export default Chart;
