import * as React from "react";
import { useCallback, useEffect, useState } from "react";
import { Container } from "./style";

interface Data {
  date: string;
  improve: {
    study: string;
    workout: string;
    reading: string;
    rest: string;
  };
  private: {
    privates: string;
    game: string;
  };
  sleeping: {
    night: string;
    nap: string;
    wake: string;
    sleep: string;
  };
  working: {
    works: string;
    houseWork: string;
  };
  entireTime: {
    entireImprove: string;
    entirePrivate: string;
    entireWorks: string;
    entireSleep: string;
  };
  id: number;
}

function Calender({
  calender,
  setCalender,
  setSelectedDate,
  data,
  selectedDate,
  finishedDay,
  setFinishedDays,
  getFormInfo,
}: any) {
  const DAYS = ["Mon", "Tue", "Wen", "Thu", "Fri", "Sat", "Sun"];

  const dayLists = DAYS.map((item, i) => {
    return (
      <li className="day" key={i}>
        {item}
      </li>
    );
  });

  useEffect(() => {
    async function getDone() {
      const response = await fetch("http://localhost:3000/timedata");
      const result = await response.json();
      const done = result.map((item: Data) => {
        if (item.date.includes(calender)) return item.date.split(".")[2];
      });
      setFinishedDays(done);
    }
    getDone();
  }, [data, calender]);

  const dateClickHandler = (date: number) => {
    if (selectedDate === date) return "selected";
    if (finishedDay.includes(String(date))) {
      return "doneDate";
    } else {
      return "date";
    }
  };

  // 월별에 따른 달력 데이터 불러오기
  let dateLists = Array(37)
    .fill("")
    .map((_, idx) => {
      const DATE = new Date(calender);
      const startDay: number = DATE.getDay() !== 0 ? DATE.getDay() : 7;
      const startMonth: number = DATE.getMonth() + 1;
      const thirtyMonth: number[] = [4, 6, 9, 11];
      const date: number = idx + 2 - startDay;

      if (startDay - 1 > idx) return <li></li>;
      if (thirtyMonth.includes(startMonth) && date > 30) return <li></li>;
      if (startMonth === 2 && DATE.getFullYear() % 4 === 0 && date > 29) {
        return <li></li>;
      } else {
        if (startMonth === 2 && DATE.getFullYear() % 4 !== 0 && date > 28)
          return <li></li>;
      }
      if (date > 31) return <li></li>;
      return (
        <li
          key={date}
          className={dateClickHandler(date)}
          onClick={() => {
            setSelectedDate(date);
            getFormInfo();
            // 바인딩 함수 추가
          }}
        >
          {date}
        </li>
      );
    });

  // 캘린더 이전 이동버튼 핸들러
  const prevHandler = () => {
    setCalender((current: string) => {
      let [year, month] = current.split(".");

      if (month === "1") {
        month = "12";
        year = String(Number(year) - 1);
        setCalender(`${year}.${month}`);
        setSelectedDate(1);
      } else {
        setCalender(`${year}.${Number(month) - 1}`);
        setSelectedDate(1);
      }
    });
  };
  // 캘린더 다음 이동버튼 핸들러
  const nextHandler = () => {
    setCalender((current: string) => {
      let [year, month] = current.split(".");

      if (month === "12") {
        month = 1;
        year = Number(year) + 1;
        setCalender(`${year}.${Number(month)}`);
        setSelectedDate(1);
      } else {
        setCalender(`${year}.${Number(month) + 1}`);
        setSelectedDate(1);
      }
    });
  };

  return (
    <Container>
      <div className="header">
        <li className="move-Btn" onClick={prevHandler}>
          <i className="fas fa-chevron-left"></i>
        </li>
        <li className="yearAndMonth">{calender}</li>
        <li className="move-Btn" onClick={nextHandler}>
          <i className="fas fa-chevron-right"></i>
        </li>
      </div>
      <div className="days">{dayLists}</div>
      <div className="dates">{dateLists}</div>
    </Container>
  );
}

export default Calender;
